const oracledb = require('oracledb');

async function runQuery() {
  let connection;
  try {
    connection = await oracledb.getConnection({
      user: process.env.ORACLE_USER || 'UCMS_db',
      password: process.env.ORACLE_PASSWORD || 'c_xps123',
      connectString: `${process.env.ORACLE_HOST || '192.168.5.127'}:${process.env.ORACLE_PORT || '1521'}/${process.env.ORACLE_SERVICE || 'ORA19CDB'}`
    });

    const result = await connection.execute(`SELECT * FROM YOUR_TABLE WHERE ROWNUM <= 10`);
    return result.rows.map(row => ({ json: row }));
  } catch (err) {
    return [{ json: { error: err.message } }];
  } finally {
    if (connection) await connection.close();
  }
}

return runQuery();
